		public function newSite($name, $short_name, $node_id, $admin_email)
		{
			global $wpdb;
			$prefix = $wpdb->get_blog_prefix(1);

			//is it a new shortname or did a site exists with the same ?
			$exist = $wpdb->get_var("SELECT count(*) FROM ".$prefix."blogs WHERE path = '/".$short_name."/'");
			
			if ($exist)
			{
				$return['status'] = false;
				$return['message'] = '<div class="error"><p>Sorry but this site already exists, try again!</p></div>';
			}
			else
			{
				
				$last = $wpdb->get_row("SELECT MAX(blog_id) as max, ".$prefix."blogs.* FROM ".$prefix."blogs");
				$next_blog_id = $last->max + 1;
				
				echo '<div class="updated below-h2" id="message"><p>Inserting in blogs table<br/>';
					$wpdb->query("	INSERT INTO `".$prefix."blogs`
									VALUES (".$next_blog_id.", 
										".$last->site_id.", 
										'".$last->domain."', 
										'/".$short_name."/',
										'".date("Y-m-d H:i:s")."',
										'".date("Y-m-d H:i:s")."',
										1,'0',0,0,0,0);");
				
					$this->createTable($prefix, $next_blog_id);
					$this->createContent($prefix, $next_blog_id, $name, $short_name, $node_id, $admin_email);
				echo '</p></div>';

				$return['status'] = $next_blog_id;
				$return['message'] = '<div class="updated below-h2" id="message"><p>Your site has <strong>succesfully</strong> been created</p></div>';
			}
			
			return $return;
		}
		
